/* Reset and base styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Poppins', sans-serif;
  line-height: 1.6;
  color: #333;
}

/* Utility classes */
.container {
  max-width: 1510px;
  margin: 0 auto;
  padding: 0 15px;
}

.d-block {
  display: block;
}

.d-none {
  display: none;
}

.d-flex {
  display: flex;
}

.justify-content-center {
  justify-content: center;
}

.align-items-center {
  align-items: center;
}

.text-center {
  text-align: center;
}

/* Responsive utilities */
@media (max-width: 768px) {
  .d-none-xs {
    display: none;
  }
  
  .d-block-xs {
    display: block;
  }
}

@media (min-width: 769px) {
  .d-block-xs {
    display: none;
  }
  
  .d-none-xs {
    display: block;
  }
}

html, body {
  max-width: 100vw;
  overflow-x: hidden;
}
img {
  max-width: 100%;
  height: auto;
  display: block;
}
@media (max-width: 900px) {
  .container, .main-content, .dashboard-flex {
    padding-left: 20px !important;
    padding-right: 20px !important;
    max-width: 100vw !important;
  }
  h1, h2, h3 { font-size: 1.2em; }
}
@media (max-width: 500px) {
  .container, .main-content, .dashboard-flex {
    padding-left: 2px !important;
    padding-right: 2px !important;
  }
  .btn, button, input, select {
    font-size: 1.05rem;
    padding: 14px 0;
  }
}

/* Filtre butonları yatay scroll */
@media (max-width: 600px) {
  .filter-bar, .filter-buttons {
    display: flex;
    overflow-x: auto;
    gap: 8px;
    padding-bottom: 8px;
  }
  .filter-bar::-webkit-scrollbar, .filter-buttons::-webkit-scrollbar {
    display: none;
  }
}

/* Kartlar mobilde tek sütun veya 2 sütun */
@media (max-width: 600px) {
  .attractions-grid {
    display: grid !important;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
  }
  .attraction-card {
    min-width: 0;
    max-width: 100%;
  }
}
@media (max-width: 400px) {
  .attractions-grid {
    grid-template-columns: 1fr;
  }
}

/* Kart başlıklarını kısalt */
.attraction-card h3, .attraction-card .title {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  font-size: 1rem;
}

/* Sabit alt bar */
.sticky-bottom-bar {
  position: fixed;
  left: 0; right: 0; bottom: 0;
  width: 100vw;
  z-index: 100;
  background: #e6004c;
  color: #fff;
