+                <label for="email">Email</label>
+                <input 
+                  type="email" 
+                  id="email" 
+                  v-model="form.email" 
+                  required
+                >
+              </div>
+              <div class="form-group">
+                <label for="subject">Subject</label>
+                <input 
+                  type="text" 
+                  id="subject" 
+                  v-model="form.subject" 
+                  required
+                >
+              </div>
+              <div class="form-group">
+                <label for="message">Message</label>
+                <textarea 
+                  id="message" 
+                  v-model="form.message" 
+                  rows="5" 
+                  required
+                ></textarea>
+              </div>
+              <button type="submit" class="btn btn-primary">Send Message</button>
+            </form>
+          </div>
+        </div>
+      </div>
+    </section>
+  </div>
+</template>
+
+<script setup>
+import { ref } from 'vue'
+
+const form = ref({
+  name: '',
+  email: '',
+  subject: '',
+  message: ''
+})
+
+const submitForm = () => {
+  console.log('Form submitted:', form.value)
+  // Add form submission logic here
+  alert('Thank you for your message! We will get back to you soon.')
+  
+  // Reset form
+  form.value = {
+    name: '',
+    email: '',
+    subject: '',
+    message: ''
+  }
+}
+</script>
+
+<style scoped>
+.contact {
+  min-height: 100vh;
+}
+
+.hero-section {
+  background: #FC6421;
+  color: white;
+  padding: 4rem 0;
+  text-align: center;
+  margin-top: 100px;
+}
+
+.hero-section h1 {
+  font-size: 3rem;
+  margin-bottom: 1rem;
+}
+
+.contact-content {
+  padding: 4rem 0;
+}
+
+.contact-grid {
+  display: grid;
+  grid-template-columns: 1fr 1fr;
+  gap: 4rem;
+}
+
+.contact-info h2,
+.contact-form h2 {
+  margin-bottom: 2rem;
+  color: #2c3e50;
+}
+
+.contact-item {
+  display: flex;
+  align-items: center;
+  gap: 1rem;
+  margin-bottom: 2rem;
+}
+
+.contact-item i {
+  font-size: 1.5rem;
+  color: #FC6421;
+  width: 30px;
+}
+
+.contact-item h3 {
+  margin-bottom: 0.5rem;
+  color: #2c3e50;
+}
+
+.form-group {
+  margin-bottom: 1.5rem;
+}
+
+.form-group label {
+  display: block;
+  margin-bottom: 0.5rem;
+  color: #2c3e50;
+  font-weight: 600;
+}
+
+.form-group input,
+.form-group textarea {
+  width: 100%;
+  padding: 0.75rem;
+  border: 1px solid #FC6421;
+  border-radius: 4px;
+  font-size: 1rem;
+  box-sizing: border-box;
+  font-family: inherit;
+}
+
+.form-group textarea {
+  resize: vertical;
+  min-height: 120px;
+}
+
+@media (max-width: 768px) {
+  .contact-grid {
+    grid-template-columns: 1fr;
+    gap: 2rem;
+  }
+}
+</style> 
